--Query
--1. Estrarre tutte le pizza con prezzo superiore a 6 euro.select P.*from Pizza Pwhere P.Prezzo > 6;--2. Estrarre la pizza più costosa.select P.*from Pizza Pwhere P.Prezzo >= all (select P.Prezzo					   from Pizza P);--3. Estrarre le pizze «bianche»--Pizze senza nessun tipo di pomodoro--select  distinct P.*--from Pizza P--join PizzaIngrediente PIN on P.Codice = PIN.CodPizza--join Ingrediente I on I.Codice = PIN.CodIngrediente--where I.Nome not like 'Pomodor%' and--	  P.Nome not in (select P.Nome--					 from Pizza P--					 join PizzaIngrediente PIN on P.Codice = PIN.CodPizza--					 join Ingrediente I on I.Codice = PIN.CodIngrediente--					 where I.Nome like 'Pomodor%');select  distinct P.*from Pizza Pjoin PizzaIngrediente PIN on P.Codice = PIN.CodPizzajoin Ingrediente I on I.Codice = PIN.CodIngredientewhere I.Nome <> 'Pomodoro' and	  P.Nome not in (select P.Nome					 from Pizza P					 join PizzaIngrediente PIN on P.Codice = PIN.CodPizza					 join Ingrediente I on I.Codice = PIN.CodIngrediente					 where I.Nome = 'Pomodoro');--4. Estrarre le pizze che contengono funghi (di qualsiasi tipo).select distinct P.*from Pizza Pjoin PizzaIngrediente PIN on P.Codice = PIN.CodPizzajoin Ingrediente I on I.Codice = PIN.CodIngredientewhere I.Nome like 'Funghi%';